var entTemp = require(process.cwd()+"/../lib/entityTemplate").entityTemplate;
var util = require("util");
var self = {};

var grafoLog = function()
{
  grafoLog.super_.call(this);

  this.moduleName = "Entity grafoLog";
  this.entityName = "grafoLog";
  this.schemaName = "grafo";
  this.tableName = "log";

  this.entityId = "id";
  this.entityIdType = this.crudUtils.INTEGER;
  this.fieldTypeObj = {date: this.crudUtils.TIMESTAMP};
}

/*
 * Inheritance
 */
util.inherits(grafoLog,entTemp);

grafoLog.prototype.init = function(opt)
{
  self = this;
  grafoLog.super_.prototype.init.call(self,opt);
}

grafoLog.prototype.insert = function(opt,callback)
{
  if (!opt.object.user_id)
    opt.object.user_id = opt.sysuser_id;

  grafoLog.super_.prototype.insert.call(self,opt,callback);
}

/*
 * Export
 */
exports.grafoLog = grafoLog;
