var entTemp = require(process.cwd()+"/../lib/entityTemplate").entityTemplate;
var util = require("util");
var self = {};

var userLayerStyle = function()
{
  userLayerStyle.super_.call(this);

  this.moduleName = "Entity userLayerStyle";
  this.entityName = "userLayerStyle";
  this.tableName = "user_layer_style";

  this.entityId = "id";
  this.entityIdType = this.crudUtils.INTEGER;
}

/*
 * Inheritance
 */
util.inherits(userLayerStyle,entTemp);

userLayerStyle.prototype.init = function(opt)
{
  self = this;
  userLayerStyle.super_.prototype.init.call(self,opt);
}

/*
 * Export
 */
exports.userLayerStyle = userLayerStyle;
